import org.apache.regexp.*;
public class RegExpTest {
	public static void main(String[] args) {
	try {	
			RE re = new RE("^(\\w){4,16}$");
			System.out.println("mathes: " + re.match(args[0]));
		}catch (RESyntaxException re) {
			re.printStackTrace();
		}
	}
}
