<%@ page import="database.*" %>
<%@ page errorPage="errorpage.jsp" %>
<%!
  private BookDBAO bookDBAO;

  public void jspInit() {	

      bookDBAO = (BookDBAO)getServletContext().getAttribute("bookDB");
      if (bookDBAO == null) {
          System.out.println("Couldn't get database.");
      }
  }

  public void jspDestroy() {      
      bookDBAO = null;
  }
%>
