public void updatePersonDetails(String collection,String personName){
			try{
				c=DatabaseManager.getCollection
("xmldb:xindice://ap_sankar:4080/db/"+collection);
				if(c==null){
					System.out.println
("Collection "+collection+" not found");
					return;
				}
				String xupdate = "<xu:modifications 
version=\"1.0\" xmlns:xu=\"http://www.xmldb.org/xupdate\"><xu:remove 
select=\"//person[fname='"+personName+"']\"/></xu:modifications>";
				System.out.println(xupdate);
				XUpdateQueryService service =
(XUpdateQueryService) c.getService("XUpdateQueryService", "1.0");
				service.update(xupdate);
				System.out.println("Modified");
			}catch(XMLDBException e){
				System.err.println("XML:DB Exception occured " 
+ e.errorCode);}
}

