 public void setPosition(String strPosition) throws JspTagException
  {
    try {
      _position = Integer.parseInt (strPosition);
    } catch (NumberFormatException ex) {
      throw new JspTagException ("The 'position' attribute must be an int: " +
ex.getMessage());
    }
  }
