bug on batik SVG DOM implementation on the SVGMatrix.getA

[svg]
<svg>
</svg>

[test-code]
import org.apache.batik.dom.svg.SVGDOMImplementation;
import org.apache.batik.dom.svg.SAXSVGDocumentFactory;
import org.apache.batik.util.XMLResourceDescriptor;
import org.w3c.dom.*;
import org.w3c.dom.svg.*;
import java.io.File;
class domtest1 {
    public static void main(String[] ar) throws Exception{
        String parser = XMLResourceDescriptor.getXMLParserClassName();
        SAXSVGDocumentFactory f = new SAXSVGDocumentFactory(parser);
        File file=new File("domtest1.svg");
        String uri = file.toURL().toString();
        SVGDocument doc = (SVGDocument)f.createDocument(uri);
        Element svg = doc.getDocumentElement();
        if(svg instanceof SVGLocatable){
            SVGLocatable locatable = (SVGLocatable)svg;
            SVGMatrix matrix = locatable.getScreenCTM();
            matrix.getA();
        }
    }
}

[test-result]
