.title {
  font-size: x-large;
  font-weight: bold;
  display: block;
/*  border: 1pt solid red;*/
}

.subtitle {
  font-size: large;
  font-weight: bold;
  display: block;
/*  border: 1pt solid red; */
}


.summary:before {
  background-color:   lightgray; 
  color: black;
  content: "Summary:  "
}
.summary {
  font-size: large;
  font-style: italic;
  display: block;
/* border: 1pt solid blue; */
}


