here is the sample code:

Using your xerces J 2_0 alpha, give following exception: 
   org.apache.xerces.dom.DOMExceptionImpl: DOM003 Namespace error

Using your xerces 1.4.1, it complains all sorts of error in xml documents which 
are perfectly fine.

import java.io.*;
import org.w3c.dom.Attr;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.NamedNodeMap;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import org.xml.sax.InputSource;
import org.w3c.dom.NamedNodeMap;
import org.xml.sax.Locator;
import org.xml.sax.helpers.*;
import org.apache.xerces.dom.NodeImpl;
//import org.apache.xerces.framework.XMLAttrList;
import org.apache.xerces.parsers.DOMParser;
//import org.apache.xerces.utils.QName;

//import org.apache.xerces.dom.*;
//import org.apache.xerces.validators.common.*;
//import org.apache.xerces.validators.schema.*;

public class SchemaValidate2 extends DOMParser  {

   /** Print writer. */
   private PrintWriter out;
   private static boolean NotIncludeIgnorableWhiteSpaces = false;
   private static final String INDENT = "  ";

   public SchemaValidate2( String inputName ) {
      //fNodeExpansion = FULL; // faster than: this.setFeature
("http://apache.org/xml/features/defer-node-expansion", false);

      try {
         //this.setFeature( "http://apache.org/xml/features/dom/defer-node-
expansion", true );

         this.setFeature( "http://apache.org/xml/features/validation/schema", 
true );
         this.setFeature( "http://xml.org/sax/features/validation", true );

         this.parse( inputName );
         out = new PrintWriter(new OutputStreamWriter(System.out, "UTF8"));
      } catch ( IOException e ) {
         System.err.println( "except" + e );
      } catch ( org.xml.sax.SAXException e ) {
         System.err.println( "except" + e );
      }
   }

   public static void main(String argv[]) {
     if ( argv.length == 0 ) {
       printUsage();
       System.exit(1);
     }
     SchemaValidate2 sv = new SchemaValidate2( argv[0] );
     Document doc = sv.getDocument();
   }

   private static void printUsage() {
      System.err.println("usage: jre SchemaValidate [filename]");
   }
}
