it will segment fault when the pointer b is null which returned by b = apr_palloc(p, sizeof(*b)) in function apr_brigade_create in file apr-util\buckets\apr_brigade.c.

APU_DECLARE(apr_bucket_brigade *) apr_brigade_create(apr_pool_t *p,
                                                     apr_bucket_alloc_t *list)
{
    apr_bucket_brigade *b;

    b = apr_palloc(p, sizeof(*b));


    b->p = p;  //!!! will be segment fault when b is null .

    b->bucket_alloc = list;

    APR_RING_INIT(&b->list, apr_bucket, link);

    apr_pool_cleanup_register(b->p, b, brigade_cleanup, apr_pool_cleanup_null);
    return b;
}
